#include "KaiXinAPICommon.h"


void* KaiXinAPI_InitialLogin_JsonParse(char *text)
{
	cJSON *json;
	cJSON *tmp;
	tResponseInitialLogin*  Response = new tResponseInitialLogin;
	memset(Response, 0 , sizeof(tResponseInitialLogin));

	json=cJSON_Parse(text);

	tmp = cJSON_GetObjectItem(json,"ret");
	if (tmp)
	{
		Response->ret = tmp->valueint;	
	}

	//Success
	if(Response->ret == 1)
	{
		tmp = cJSON_GetObjectItem(json,"verify");
		if (tmp)
		{
			STRCPY_Ex(Response->verify,tmp->valuestring);
		}

		tmp = cJSON_GetObjectItem(json,"wapverify");
		if (tmp)
		{
			STRCPY_Ex(Response->wapverify,tmp->valuestring);
		}

		tmp = cJSON_GetObjectItem(json,"uid");
		if (tmp)
		{
			if(tmp->valueint > 0)
				sprintf(Response->uid, "%d", tmp->valueint);
			else
				STRCPY_Ex(Response->uid, tmp->valuestring);
		}
	}
	//Failue
	else
	{
		tmp = cJSON_GetObjectItem(json,"msg");
		if (tmp)
		{
			STRCPY_Ex(Response->msg, tmp->valuestring);	
		}
	}
	cJSON_Delete(json);
	return Response;
}





