#include "KaiXinAPICommon.h"

void* KaiXinAPI_StatusFaceList_JsonParse(char *text)
{
	cJSON *json;
	cJSON *pTemp0;
	tResponseStatusFaceList*  Response = new tResponseStatusFaceList;
	memset(Response, 0 , sizeof(tResponseStatusFaceList));

	json = cJSON_Parse(text);

	pTemp0 = cJSON_GetObjectItem(json,"ret");
	if (pTemp0)
	{
		Response->ret = pTemp0->valueint;	
	}

	//Success
	if(Response->ret == 1)
	{
		pTemp0 = cJSON_GetObjectItem(json, "uid");
		if(pTemp0)
		{
			Response->uid = pTemp0->valueint;
		}
		pTemp0 = cJSON_GetObjectItem(json, "faces");
		if(pTemp0)
		{
			int nSize1 = 0, i = 0;
			nSize1 = cJSON_GetArraySize(pTemp0);
			Response->nSize_faces = nSize1;
			if( nSize1 != 0 )
			{
				Response->faces = NULL;
				Response->faces = (StatusFaceList_faces*) malloc(sizeof( StatusFaceList_faces ) * nSize1 );
				memset(Response->faces, 0 , sizeof(StatusFaceList_faces) * nSize1 );
			}
			for ( i = 0; i < nSize1; i++ )
			{
				cJSON *Item1 = NULL, *pTemp1 = NULL;
				Item1 = cJSON_GetArrayItem(pTemp0,i);
				pTemp1 = cJSON_GetObjectItem(Item1, "sface");
				if(pTemp1)
				{
					Response->faces[i].sface = pTemp1->valueint;
				}
				pTemp1 = cJSON_GetObjectItem(Item1, "slogo");
				if(pTemp1)
				{
					STRCPY_Ex(Response->faces[i].slogo, pTemp1->valuestring);
				}
				pTemp1 = cJSON_GetObjectItem(Item1, "ntype");
				if(pTemp1)
				{
					Response->faces[i].ntype = pTemp1->valueint;
				}

			}
		}

	}
	//Failue
	else
	{
		//pTemp0 = cJSON_GetObjectItem(json,"msg");
		//if (pTemp0)
		//{
		//	strcpy(Response->msg, pTemp0 ->valuestring);	
		//}
	}
	cJSON_Delete(json);
	return Response;
}



